-- Seed Exercises
INSERT INTO exercises (title, category, duration, mood_type, description, instructions) VALUES
('Box Breathing', 'Breathing', 5, 'Anxious', 'A simple breathing technique to calm your nervous system.', '1. Inhale through your nose for 4 seconds.\n2. Hold your breath for 4 seconds.\n3. Exhale through your mouth for 4 seconds.\n4. Hold for 4 seconds before inhaling again.'),

('5-4-3-2-1 Grounding', 'Grounding', 10, 'Overwhelmed', 'Use your 5 senses to bring yourself back to the present moment.', '1. Acknowledge 5 things you see around you.\n2. Acknowledge 4 things you can touch or feel.\n3. Acknowledge 3 things you can hear.\n4. Acknowledge 2 things you can smell.\n5. Acknowledge 1 thing you can taste.'),

('Safe Place Visualization', 'Mindfulness', 15, 'Stressed', 'Mental imagery to create a safe mental refuge.', '1. Close your eyes and imagine a place where you feel perfectly safe and guarding.\n2. Visualize the colors, sounds, and smells.\n3. Stay in this place for as long as you need.'),

('Progressive Muscle Relaxation', 'Relaxation', 20, 'Tense', 'Systematically tensing and relaxing muscle groups.', '1. Start with your toes, tense them for 5 seconds, then release.\n2. Move to your calves, tense and release.\n3. Work your way up your body to your head.\n4. Notice the difference between tension and relaxation.'),

('Cognitive Reframing', 'CBT', 15, 'Low', 'Challenge negative thoughts and replace them with balanced ones.', '1. Identify a negative thought you are having.\n2. Ask yourself: Is this 100% true? Is there another explanation?\n3. Re-write the thought in a more neutral or positive way.'),

('Gratitude Journaling', 'Journaling', 10, 'Sad', 'Shift focus to positive aspects of life.', '1. Write down 3 things you are grateful for today.\n2. They can be big (family) or small (a good cup of coffee).\n3. Reflect on why you are grateful for them.'),

('Mindful Walking', 'Mindfulness', 20, 'Restless', 'Meditation in motion.', '1. Walk at a natural pace.\n2. Focus on the sensation of your feet touching the ground.\n3. Notice the rhythm of your breath.\n4. If your mind wanders, gently bring it back to your steps.'),

('Loving-Kindness Meditation', 'Meditation', 15, 'Lonely', 'Cultivate compassion for yourself and others.', '1. Sit comfortably.\n2. Repeat silently: "May I be happy. May I be well. May I be safe."\n3. Extend this wish to a loved one, then a neutral person, then a difficult person, and finally all beings.');
